@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&family=Roboto:wght@300;400;500;700&display=swap');

:root {
    --primary-orange: #FF6600;
    --primary-orange-light: #FF8533;
    --tech-black: #1A1A1A;
    --cyber-blue: #00D4FF;
    --cyber-blue-dark: #00A8CC;
    --light-grey: #F8F9FA;
    --dark-grey: #333333;
    --success-green: #28A745;
    --purple-gradient: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    --orange-gradient: linear-gradient(135deg, #FF6600 0%, #FF8533 100%);
    --blue-gradient: linear-gradient(135deg, #00D4FF 0%, #00A8CC 100%);
    --card-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
    --hover-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
    --glass-bg: rgba(255, 255, 255, 0.1);
    --glass-border: rgba(255, 255, 255, 0.2);
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Inter', 'Roboto', sans-serif;
    background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
    background-attachment: fixed;
    color: var(--dark-grey);
    min-height: 100vh;
    position: relative;
}

body::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: 
        radial-gradient(circle at 20% 50%, rgba(255, 102, 0, 0.1) 0%, transparent 50%),
        radial-gradient(circle at 80% 80%, rgba(0, 212, 255, 0.1) 0%, transparent 50%);
    pointer-events: none;
    z-index: 0;
}

.container-fluid {
    position: relative;
    z-index: 1;
}

#loading-screen {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: var(--tech-black);
    display: flex;
    align-items: center;
    justify-content: center;
    z-index: 9999;
    opacity: 1;
    transition: opacity 0.5s ease;
}

#loading-screen.hidden {
    opacity: 0;
    pointer-events: none;
}

.loading-content {
    text-align: center;
    color: white;
}

.loading-spinner {
    width: 60px;
    height: 60px;
    border: 4px solid rgba(255, 102, 0, 0.3);
    border-top: 4px solid var(--primary-orange);
    border-radius: 50%;
    animation: spin 1s linear infinite;
    margin: 0 auto 20px;
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

.modern-navbar {
    background: linear-gradient(135deg, var(--tech-black) 0%, #2A2A2A 100%);
    backdrop-filter: blur(10px);
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.3);
    padding: 0.75rem 0;
    z-index: 1030;
}

.modern-brand {
    font-size: 1.5rem;
    font-weight: 700;
    transition: all 0.3s ease;
}

.brand-text {
    background: linear-gradient(135deg, var(--primary-orange), var(--cyber-blue));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.modern-brand:hover {
    transform: scale(1.05);
}

.modern-navbar .nav-link {
    color: rgba(255, 255, 255, 0.85);
    font-weight: 500;
    padding: 0.5rem 1rem;
    border-radius: 8px;
    transition: all 0.3s ease;
    position: relative;
}

.modern-navbar .nav-link:hover {
    color: var(--primary-orange);
    background: rgba(255, 102, 0, 0.1);
    transform: translateY(-2px);
}

.modern-navbar .nav-link.active {
    color: var(--primary-orange);
    background: rgba(255, 102, 0, 0.15);
}

.modern-navbar .nav-link.active::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 30px;
    height: 3px;
    background: var(--primary-orange);
    border-radius: 2px;
}

.mega-dropdown .mega-menu {
    min-width: 250px;
    padding: 0.5rem 0;
    background: rgba(26, 26, 26, 0.98);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 102, 0, 0.2);
    border-radius: 12px;
    margin-top: 0.5rem;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5);
}

.mega-menu .dropdown-item {
    color: rgba(255, 255, 255, 0.8);
    padding: 0.75rem 1.5rem;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 0.75rem;
}

.mega-menu .dropdown-item:hover {
    background: rgba(255, 102, 0, 0.15);
    color: var(--primary-orange);
    transform: translateX(5px);
}

.mega-menu .dropdown-item i {
    font-size: 1.1rem;
}

